import React, { Fragment } from 'react';
import { Tabs, Tab } from '@material-ui/core';
import { Link, Route, Switch, BrowserRouter as Router } from 'react-router-dom/Route';
import Home from './../SS/Home';
import Sarees from './../SS/Sarees';


const SSTabs = (props) => {
   const value = props.value;
   const handler = props.handleChange;
   return (
     <Fragment>
      <Tabs indicatorColor="primary" centered value={value} onChange={handler}>
      {
 props.tabs.map(
   ({label, path})=><Tab key={label}
                         label={label}
                         component={Link}
                         to={path} />
 )
}
